<?php
$jump_url = rtrim ($_GET[ 'jump_url' ],'/');
$auto_jump_time = $zbp->Config ( 'ExternalLinkSecurityJump' )->auto_jump_time;

$urlData = $zbp->Config ( 'ExternalLinkSecurityJump' )->data;
$urlData = is_array ($urlData) ? $urlData : [];
$isHave = false;
$title = '';
foreach ( $urlData as $v ) {
    if ( strpos ( $jump_url , $v[ 'url' ] ) !== false ) {//存在
        $title = $v[ 'title' ];
        $isHave = true;
        continue;
    }
}
